        // TODO improve gas efficiency by using assembly code
        bytes memory raw = new bytes(num.val.length + 1);

        if (num.neg) {
            raw[0] = 0x01;
        }

        uint index = 1;
        for (uint i = 0; i < num.val.length; i++) {
            raw[index] = num.val[i];
            index++;
